hljs.registerLanguage("bash",(()=>{"use strict";return e=>({
    name:"Bash",
    aliases:["sh","zsh"],
    keywords:{
        keyword:
            "if then else elif fi for while in do done case esac function",
        literal:
            "true false",
        built_in:
            // Commonly used commands
            "echo printf cd pwd ls cat grep mv cp rm mkdir rmdir touch chmod chown ln find ps kill"
    },
    contains:[
        e.HASH_COMMENT_MODE,
        e.APOS_STRING_MODE,
        e.QUOTE_STRING_MODE,
        e.C_NUMBER_MODE,
        {
            className:"variable",
            variants:[
                {begin:/\$\w+/},
                {begin:/\$\{/, end:/\}/}
            ]
        },
        {
            className:"function",
            beginKeywords:"function",
            end:/\s*\{/,
            excludeEnd:true,
            contains:[e.TITLE_MODE]
        },
        {
            className:"command",
            begin:/\b[a-z][\w-]*\b/,
            relevance:0
        }
    ]
})})());
