hljs.registerLanguage("css",(()=>{"use strict";return e=>({
    name:"CSS",
    case_insensitive:!0,
    illegal:/[=<>'"$]/,
    contains:[
        e.C_BLOCK_COMMENT_MODE,
        {
            className:"selector-id",
            begin:/#[A-Za-z0-9_-]+/
        },
        {
            className:"selector-class",
            begin:/\.[A-Za-z0-9_-]+/
        },
        {
            className:"selector-attr",
            begin:/\[/, end:/\]/,
            illegal:/["$]/
        },
        {
            className:"selector-pseudo",
            begin:/:/,
            end:/[;}{]/,
            illegal:/\W/
        },
        {
            className:"property",
            end:/:|;/,
            illegal:/\n/,
            relevance:0,
            contains:[
                e.C_BLOCK_COMMENT_MODE,
                {
                    className:"value",
                    endsWithParent:!0,
                    excludeEnd:!0,
                    contains:[
                        e.C_BLOCK_COMMENT_MODE,
                        {
                            className:"function",
                            begin:/[A-Za-z0-9_-]+\(/,
                            end:/\)/,
                            contains:[e.APOS_STRING_MODE,e.QUOTE_STRING_MODE,e.C_NUMBER_MODE]
                        },
                        e.APOS_STRING_MODE,
                        e.QUOTE_STRING_MODE,
                        e.C_NUMBER_MODE,
                        {
                            className:"keyword",
                            begin:/!important/
                        }
                    ]
                }
            ]
        },
        {
            begin:/@/,
            end:/[;}{]/,
            illegal:/[:?@\-\s]/,
            contains:[
                {
                    className:"keyword",
                    begin:/[A-Za-z0-9_-]+/
                },
                {
                    begin:/\s/,
                    relevance:0,
                    contains:[
                        e.APOS_STRING_MODE,
                        e.QUOTE_STRING_MODE,
                        e.C_NUMBER_MODE
                    ]
                }
            ]
        }
    ]
})})());
