hljs.registerLanguage("json",(()=>{"use strict";return e=>({
    name:"JSON",
    contains:[
        e.QUOTE_STRING_MODE,
        e.C_NUMBER_MODE,
        {
            className:"literal",
            beginKeywords:"true false null"
        },
        {
            begin:/\{/, end:/\}/,
            contains:[
                {
                    className:"attr",
                    begin:/"/, end:/"/,
                    contains:[e.BACKSLASH_ESCAPE],
                    illegal:"\\n"
                },
                {
                    begin:/:/,
                    end:/,|}/,
                    contains:[e.INHERIT(e.QUOTE_STRING_MODE),e.C_NUMBER_MODE,e.BOOLEAN_MODE],
                    relevance:0
                }
            ],
            illegal:"\\S"
        },
        {
            begin:/\[/, end:/\]/,
            contains:[
                e.INHERIT(e.QUOTE_STRING_MODE),
                e.C_NUMBER_MODE,
                e.BOOLEAN_MODE
            ],
            illegal:"\\S"
        }
    ]
})})());
